import { AUTH } from '../constants/actionTypes';
import * as api from '../api/index.js';

async function signin(formData, router){
  try {
    const { data } = await api.signIn(formData);

    return dispatch({ type: AUTH, data });

    router.push('/');
  } catch (error) {
    console.log(error);
  }
};

async function signup(formData, router){
  try {
    const { data } = await api.signUp(formData);

    return dispatch({ type: AUTH, data });

    router.push('/');
  } catch (error) {
    console.log(error);
  }
};

export {signin, signup}

